import React, { Component } from "react"
import * as d3 from "d3"
import { rgb } from "d3-color";

class Snapshots extends Component {
    constructor(props) {
        super(props)
        this.state={
            grahfi:null,
        }
    }

    componentWillReceiveProps(props) {
        const snapshots = props.snapshots
        const snapshotSVG = d3.select("#snapshot")
        const padding = 100
        const width = snapshotSVG.node().parentNode.clientWidth
        snapshotSVG.attr("width", width).attr("height", width).attr("fill","black")
        const max = {}
        const min = {}
        max.x = d3.max(snapshots, snpst => snpst.vector[0])
        max.y = d3.max(snapshots, snpst => snpst.vector[1])
        min.x = d3.min(snapshots, snpst => snpst.vector[0])
        min.y = d3.min(snapshots, snpst => snpst.vector[1])
    var legendSVG = d3.select("#legend")
    legendSVG.append('g');
    var line = legendSVG.append('line')
        .attr('x1', 0)
        .attr('y1', 2)
        .attr('x2', 15)
        .attr('y2', 2)
        .attr('stroke-width', 5);
    var text = legendSVG.append('text')
        .attr('class', 'legend-label')
        .attr("dy", -13)
        .style("text-anchor", "start")
        .attr('fill', "Black")
        .attr('font-size', '13')
        .attr("transform", "translate(" + 18 + "," + 20 + ")");             


        const xScale = d3
            .scaleLinear()
            .domain([min.x, max.x])
            .range([padding, width - padding])
        const yScale = d3
            .scaleLinear()
            .domain([min.y, max.y])
            .range([padding, width - padding])

        const snapshotLinkData = []
        for (let i = 0; i < snapshots.length - 1; i++) {
            snapshotLinkData.push([
                snapshots[i].vector,
                snapshots[i + 1].vector
            ])
        }
        const snapshotLink = snapshotSVG
            .selectAll("line")
            .data(snapshotLinkData)
        snapshotLink.exit().remove()
        snapshotLink
            .enter()
            .append("line")
            .attr("x1", d => xScale(d[0][0]))
            .attr("x2", d => xScale(d[1][0]))
            .attr("y1", d => yScale(d[0][1]))
            .attr("y2", d => yScale(d[1][1]))
            .attr("stroke", "#d9dde2")
            .attr("stroke-width", 3)

        const pointsData = snapshots.map(snpst => snpst.vector)
        const points = snapshotSVG.selectAll("circle").data(pointsData)
        points.exit().remove()
        points
            .enter()
            .append("circle")
            .attr("cx", d => xScale(d[0]))
            .attr("cy", d => yScale(d[1]))
            .attr("r", 5)
            .attr("fill", function(data,i){
                if(i < 100){ return "rgb(80,255,"+(255 - i*2)+")";}
                else{return "rgb(80,"+(255 - i*2 +200)+",0)";}
            })
            .attr("stroke",function(data,i){
                if(i < 100){ return "rgb(80,255,"+(255 - i*2)+")";}
                else{return "rgb(80,"+(255 - i*2 + 200)+",0)";}

            })
            .on("click", (d, i) => {
                this.setState({
                    grahfi:i,
                });
                // console.log(this.state.grahfi)
                //console.log(d, i);

            })



    }
    render() {
        return <svg id="snapshot" onClick={
                // console.log(this.state.grahfi),
                this.props.news.getChildData.bind(this,this.state.grahfi)
        }
        />
    }
}

export default Snapshots
